import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "../css/";

const Carousel = () => {
  const settings = {
    dots: true, // Menampilkan titik navigasi
    infinite: true, // Carousel berputar terus menerus
    speed: 500, // Kecepatan transisi
    slidesToShow: 1, // Jumlah slide yang ditampilkan
    slidesToScroll: 1, // Jumlah slide yang bergeser saat berpindah
    autoplay: true, // Aktifkan autoplay
    autoplaySpeed: 3000, // Waktu antar slide
  };

  return (
    <div style={{ width: "100%", margin: "0",  }}>
      <h2> Simple Carousel </h2>
      <Slider {...settings}>
        <div>
          <img src="https://via.placeholder.com/800x400" alt="Slide 1" />
        </div>
        <div>
          <img src="https://via.placeholder.com/800x400" alt="Slide 2" />
        </div>
        <div>
          <img src="https://via.placeholder.com/800x400" alt="Slide 3" />
        </div>
      </Slider>
    </div>
  );
};

export default Carousel;
