import React, { useState } from 'react';
import '../App.css';
import '../css/Dashboard.css';
import LogoHeader from '../component/LogoHeader';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import { Popup } from "../cms/component_cms";
import { ButtonDashboard } from './component_cms/ButtonDashboard';



function Dashboard() {
  const [popupVisible, setPopupVisible] = useState(false);
  const [popupText, setPopupText] = useState('');

  const handleButtonClick = (text) => {
    setPopupText(text);
    setPopupVisible(true);
  };

  const closePopup = () => {
    setPopupVisible(false);
    setPopupText('');
  };

  return (
    <div className="app-container">   
      <header className="app-header">
        <LogoHeader />
        <ButtonDashboard text="Update Carousel" onClick={() => handleButtonClick('Update Carousel')} />
        <ButtonDashboard text="Update Calendar" onClick={() => handleButtonClick('Update Calendar')} />
        <ButtonDashboard text="Update Event" onClick={() => handleButtonClick('Update Event')} />
      </header>

      {popupVisible && <Popup  onClose={closePopup} />}
    </div>
  );
}

export default Dashboard;
