import React, { useState } from 'react';
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBIcon,
  MDBInput
} from 'mdb-react-ui-kit';
import axios from 'axios'; // Install axios jika belum
import logo from '../img/the_people_logo_2_top 2 copy.png';
import '../css/Login.css';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login', {
        email,
        password
      });
      setMessage(response.data.message);
      localStorage.setItem('token', response.data.token); // Simpan token
    } catch (error) {
      setMessage(error.response?.data?.error || 'Something went wrong!');
    }
  };

  return (
    <MDBContainer fluid>
      <MDBRow>
        <MDBCol sm='6' style={{ overflow: 'hidden' }}>
          <div className='d-flex flex-row ps-0 pe-5 pt-5'>
            <MDBIcon fas icon="crow fa-3x me-3" style={{ color: '#709085' }} />
            <span className="h1 fw-bold mb-0">
              <img className='logo-login' src={logo} alt="Logo" />
            </span>
          </div>

          <div className='d-flex flex-column justify-content-center h-custom-2 w-75 pt-4'>
            <h3 className="fw-normal mb-3 ps-5 pb-3" style={{ letterSpacing: '1px' }}>Log in</h3>

            <MDBInput
              wrapperClass='mb-4 mx-5 w-100'
              label='Email address'
              id='formControlLg'
              type='email'
              size="lg"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <MDBInput
              wrapperClass='mb-4 mx-5 w-100'
              label='Password'
              id='formControlLg'
              type='password'
              size="lg"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />

            <MDBBtn
              className="mb-4 px-5 mx-5 w-100"
              color='info'
              size='lg'
              onClick={handleLogin}
            >
              Login
            </MDBBtn>
            <p className="small mb-5 pb-lg-3 ms-5">
              <a className="text-muted" href="#!">Forgot password?</a>
            </p>
            <p className='ms-5'>Don't have an account? <a href="#!" className="link-info">Register here</a></p>
          </div>
        </MDBCol>

        <MDBCol sm='6' className='d-none d-sm-block px-0'>
          <img
            src="https://i.pinimg.com/1200x/59/47/33/5947333920d8f09737d017ecfef27b1c.jpg"
            alt="Login"
            className="w-100"
            style={{ objectFit: 'cover', objectPosition: 'left' }}
          />
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
}

export default App;
