export function PopupUpdateEvent({
  text,
  isOpen,
  onClose,
  title,
  eventName,
  eventDate,
  eventType,
  eventLink,
  onSave, // Tambahkan prop untuk menangani tombol "Save"
}) {
  const [formValues, setFormValues] = useState({
    eventName: eventName || '',
    eventDate: eventDate || '',
    eventType: eventType || '',
    eventLink: eventLink || '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues((prevValues) => ({
      ...prevValues,
      [name]: value,
    }));
  };

  const handleSave = () => {
    if (onSave) {
      onSave(formValues);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal show d-block" tabIndex="-1" role="dialog" onClick={onClose}>
      <div className="modal-dialog" onClick={(e) => e.stopPropagation()}>
        <div className="background-popup">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">
                <b>{title}</b> Update Event
              </h5>
              <button type="button" className="btn-close" onClick={onClose}></button>
            </div>
            <div className="popup-body">
              <div
                className="modal-body-cms"
                style={{
                  backgroundColor: 'transparent',
                  justifyContent: 'unset',
                  alignItems: 'unset',
                }}
              >
                <label htmlFor="eventName" style={{ color: 'black' }}>
                  Event Name
                </label>
                <input
                  type="text"
                  id="eventName"
                  className="modal-body-input"
                  style={{ color: 'black', backgroundColor: 'white', border: '1px solid black' }}
                  name="eventName"
                  placeholder="Enter Event Name"
                  value={formValues.eventName}
                  onChange={handleChange}
                />

                <label htmlFor="eventDate" style={{ color: 'black' }}>
                  Event Date
                </label>
                <input
                  type="date"
                  id="eventDate"
                  className="modal-body-input"
                  style={{ color: 'black', backgroundColor: 'white', border: '1px solid black' }}
                  name="eventDate"
                  value={formValues.eventDate}
                  onChange={handleChange}
                />

                <label htmlFor="eventType" style={{ color: 'black' }}>
                  Event Type
                </label>
                <EventType
                  selectedType={formValues.eventType}
                  onTypeChange={(value) =>
                    setFormValues((prev) => ({ ...prev, eventType: value }))
                  }
                />

                <label htmlFor="eventLink" style={{ color: 'black' }}>
                  Event Link
                </label>
                <input
                  type="text"
                  id="eventLink"
                  className="modal-body-input"
                  style={{ color: 'black', backgroundColor: 'white', border: '1px solid black' }}
                  name="eventLink"
                  placeholder="Enter Event Link"
                  value={formValues.eventLink}
                  onChange={handleChange}
                />
              </div>
              <button className="save-button" onClick={handleSave}>
                Save
              </button>
            </div>
            <div className="modal-footer"></div>
          </div>
        </div>
      </div>
    </div>
  );
}
