import React, { useEffect, useState } from "react";
import { Carousel } from "react-bootstrap";
import axios from "axios";


const MyCarousel = () => {
  const [images, setImages] = useState([]); // State untuk menyimpan data gambar

  useEffect(() => {
    // Fungsi untuk mendapatkan data gambar dari API
    const fetchImages = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/get-all-images"); // Pastikan URL endpoint API Anda sesuai
        setImages(response.data.images); // Simpan data gambar ke state
      } catch (error) {
        console.error("Error fetching images:", error);
      }
    };

    fetchImages();
  }, []);

  return (
    <Carousel
      className="custom-carousel"
      interval={3000}
      indicators={true}
      controls={true}
      fade={true}
    >
      {images.map((image) => (
        <Carousel.Item key={image.id}> {/* Menggunakan UUID sebagai key */}
          <img
            className="d-block w-100 custom-image"
            src={image.image}  // Menggunakan image.base64 dari API
            alt={`Slide with ID ${image.id}`}
            style={{ height: "79vh", objectFit: "cover" }}
          />
        </Carousel.Item>
      ))}
    </Carousel>
  );
};

export default MyCarousel;
