                {/* Tampilkan gambar pratinjau jika ada */}
                {preview ? (
                  <img className="img-preview" src={preview} alt="Preview" />
                ) : (
                  <>
                    <img className="img-upload" src={upload} alt="Upload icon" />
                    <p style={{ textAlign: 'center' }}>
