export function PopupUpdateEvent({ text, isOpen, onClose, title}) {

  const fileInputRef = useRef(null);
  const [formData, setFormData] = useState({
    eventName: "",
    eventDate: "",
    eventType: "Select Type",
    eventLink: "",
    eventMonth: "Select Month",
    imageBase64: null,
    preview: null,
  });
  const handleUploadClick = () => {
    fileInputRef.current.click();
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const fileURL = URL.createObjectURL(file);
      setFormData((prev) => ({ ...prev, preview: fileURL }));

      // Convert to Base64
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData((prev) => ({ ...prev, imageBase64: reader.result.split(",")[1] }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    const { eventName, eventDate, eventType, eventLink, eventMonth, imageBase64 } = formData;

    if (eventName && eventDate && eventType !== "Select Type" && imageBase64) {
      try {
        const response = await axios.post(
          `${API_URL}/save-event`,
          { eventName, eventDate, eventType, eventLink, eventMonth, image: imageBase64 },
          { headers: { "Content-Type": "application/json" } }
        );
        console.log("Event saved successfully:", response.data);
        onClose(); // Menutup popup setelah berhasil disimpan
      } catch (error) {
        console.error("Error saving event:", error);
      }
    } else {
      alert("Please fill all required fields!");
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal show d-block" tabIndex="-1" role="dialog" onClick={onClose}>
      <div className="modal-dialog" onClick={(e) => e.stopPropagation()}>
        <div className="background-popup">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">
                <b>{title}</b> Update Carousel
              </h5>
              <button type="button" className="btn-close" onClick={onClose}></button>
            </div>
            <div className="popup-body" >
            <div className="modal-body-cms" style={{backgroundColor:'transparent', justifyContent:"unset", alignItems:'unset', justifyContent:"right"}}>
            
            <label style={{color:"black"}} id="event_name">Event Name</label>
            <input type="text"
                  name="eventName"
                  className="modal-body-input"
                  style={{ color: "black", backgroundColor: "white", border: "1px solid black" }}
                  placeholder="Enter Event Name"
                  value={formData.eventName}
                  onChange={handleChange}></input>
            
            <label id="event_date" style={{color:"black"}}>Add Event Date</label>
            <input  type="date"
                  name="eventDate"
                  className="modal-body-input"
                  style={{ color: "black", backgroundColor: "white", border: "1px solid black" }}
                  value={formData.eventDate}
                  onChange={handleChange}></input>
            
            <label style={{ color: "black" }}>Event Type</label>
                <EventType
                  selectedType={formData.eventType}
                  setSelectedType={(value) =>
                    setFormData((prev) => ({ ...prev, eventType: value }))
                  }
                />
            
            <label style={{ color: "black" }}>Event Month</label>
                <MonthDropdown
                  selectedMonth={formData.eventMonth}
                  setSelectedMonth={(value) =>
                    setFormData((prev) => ({ ...prev, eventMonth: value }))
                  }
                />
                
            </div>
            <button className="save-button" onClick={handleSave}>
                Save
              </button>
            </div>
            <div className="modal-footer"></div>
          </div>
        </div>
      </div>
    </div>
  );
}
